      <div className='movie-img'>
        
        {movie.Poster ?<img src={movie.Poster} alt={movie.Title} />:`${movie.Title}` }
      </div>
      <div className='movie-information'>
        <div className='text-info'>
          <h3>{movie.Title}</h3>
          {movie.Year ? <p>{movie.Year}</p>:"..."}
        </div>

        <div className='btns'>
          <button className='btn' disabled={storedMovie} onClick={()=>movieContext.moviesDispatch(
            {type:actions.Add_MOVIE_TO_WATCHLIST,payload:movie}
          )}>Add To WatchList</button>
          <button className='btn' onClick={()=>movieContext.moviesDispatch(
            {type:actions.Add_MOVIE_TO_WATCHED,payload:movie}
          )}>Add To Watched</button>
        </div>
      </div>
    </div>
  )
}

export default Moviecard
