import './moviecard.css';
import * as actions from '../../context/ActionsType';
import React from 'react'
import {useContextProvider}from '../../context/Contextapi';
const MovieCard = ({movie}) => {
  const movieaction=useContextProvider();
  const storedMovie=movieContext.watchlist.find((o)=>{
    o.imdbID=movie.imdbID
  })
  const storedMoviewatched=movieContext.watchlist.find(
    (o)=>{o.imdbID=movie.imdbID}
  )
  const watchlistdisabled=storedMovie?true:storedMoviewatched?true:false;
  const watcheddisabled=storedMoviewatched?true:false;
  return (
    <div className='movie-card'>
      <div className="movie-img">
        <img src={movie.Poster} alt="" />
      </div>
      <div className='movie-information'>
        <div className="movie-info">
          <h3>{movie.Title}</h3>
          <p>{movie.Year}</p>
        </div>
        <div className="controls-btn">
          <button className='btn'
          disabled={watchlistdisabled}
          onClick={()=>movieaction.moviesDispatch(
            {type:actions.Add_MOVIE_TO_WATCHLIST,payload:movie}
          )}
          >
            ADD TO WATCHEDLIST
          </button>
          <button className='btn'
          disabled={watcheddisabled}
          onClick={()=>movieaction.moviesDispatch({type:actions.REMOVE_MOVIE_FROM_WATCHED,payload:movie})}
          >
            ADD TO WATCHED
          </button>
        </div>
      </div>
    </div>
  )
}

export default MovieCard;
