import * as actions from './ActionsType';
export const reducer=(state,action)=>{
  switch(action.type){
    case actions.Add_MOVIE_TO_WATCHLIST:
      return {
        ...state,
        watchlist:[action.payload,...state],
      };
    default :
    return state;  
  }
}
