import React from 'react'
import './moviecontrols.css'
import {useMoviesContext} from '../../context/GlobalContext';
import * as actions from '../../context/ActionTypes';
const MovieControls = ({movie,type}) => {
  const remove=useMoviesContext();
  return (  
    <div className='inner-card-controls'>
      {
        type==="watchlist"&&(
          <>
            <button className='ctrl-btn' onClick={()=>{
              remove.moviesDispatch({type:actions.Add_MOVIE_TO_WATCHED,
                payload:movie})
            }}> 
              <i class="fa-solid fa-eye"/>
            </button>
            <button className='ctrl-btn' onClick={()=>{
              remove.moviesDispatch({type:actions.REMOVE_MOVIE_FROM_WATCHLIST,payload:movie.imdbID})
            }}>
              <i className='fa-fw fa fa-times'/>
            </button>
          </>
        )
        }
        {
        type==="watched"&&(
          <>
            <button className='ctrl-btn' onClick={()=>{
              remove.moviesDispatch({type:actions.REMOVE_MOVIE_FROM_WATCHED,payload:movie})
            }}> 
              <i className="fa-solid fa-eye-slash"/>
            </button>
            <button className='ctrl-btn' onClick={()=>{
              remove.moviesDispatch({type:actions.REMOVE_MOVIE_FROM_WATCHED,payload:movie.imdbID})
            }}>
              <i className='fa-fw fa fa-times'/>
            </button>
          </>
        )
      }
    </div>
  )
}

export default MovieControls
